import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function BarChart({ chartData }) {
	return (
		<div>
			<p>
				The climate crisis is a health crisis. Air pollution kills an estimated
				7 million people every year, while climate change causes more extreme
				weather events, exacerbates malnutrition and fuels the spread of
				infectious diseases such as malaria. The same emissions that cause
				global warming are responsible for more than one-quarter of deaths from
				heart attack, stroke, lung cancer and chronic respiratory disease.
				Leaders in both the public and private sectors must work together to
				clean up our air and mitigate the health impacts of climate change.
			</p>
			<Bar data={chartData} width={300} height={100} />
		</div>
	)};

export default BarChart;
