import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import LineChart from './LineChart';
import logo from './Assets/logo.png';

function Home(props) {
	let [usState, setUsState] = useState([]);

	let [age, setAge] = useState([]);
	let [gender, setGender] = useState([]);
	let [sleep, setSleep] = useState([]);
	let [weight, setWeight] = useState([]);
	let [idealWeight, setIdealWeight] = useState([]);
	let [height, setHeight] = useState([]);
	let [bmi, setBmi] = useState([]);
	let [bmr, setBmr] = useState([]);
	let [tdee, setTdee] = useState([]);
	let [bodyFat, setBodyFat] = useState([]);
	let [result, setResult] = useState([]);
	let [alert, setAlert] = useState([]);

	let states = {
		Hawaii: 82.3,
		California: 81.7,
		NewYork: 81.4,
		Minnesota: 80.9,
		Connecticut: 80.9,
		Massachusetts: 80.6,
		Colorado: 80.6,
		NewJersey: 80.5,
		Washington: 80.4,
		Florida: 80.2,
		Utah: 80.1,
		Arizona: 80.0,
		Oregon: 79.9,
		Guam: 79.9,
		RhodeIsland: 79.8,
		Vermont: 79.8,
		PuertoRico: 79.8,
		NorthDakota: 79.7,
		NewHampshire: 79.7,
		Nebraska: 79.6,
		USVirginIslands: 79.6,
		Virginia: 79.5,
		Wisconsin: 79.5,
		Iowa: 79.4,
		Idaho: 79.4,
		Illinois: 79.4,
		Texas: 79.2,
		Maryland: 79.2,
		Alaska: 79.0,
		DistrictofColumbia: 79.0,
		SouthDakota: 78.9,
		Wyoming: 78.9,
		Montana: 78.9,
		UnitedStates: 78.8,
		Maine: 78.7,
		Nevada: 78.7,
		Kansas: 78.5,
		Delaware: 78.5,
		Pennsylvania: 78.4,
		NorthCarolina: 78.1,
		NewMexico: 78.0,
		Michigan: 78.1,
		Georgia: 77.9,
		Missouri: 77.3,
		SouthCarolina: 77.1,
		Indiana: 77.1,
		Ohio: 77.0,
		NorthernMarianaIslands: 76.1,
		Louisiana: 76.1,
		Tennessee: 76.0,
		Oklahoma: 76.0,
		Arkansas: 76.0,
		Kentucky: 75.6,
		Alabama: 75.5,
		Mississippi: 74.9,
		AmericanSamoa: 74.8,
		WestVirginia: 74.8,
	};
	let calBmi = (e) => {
		e.preventDefault();
		if (weight !== [] || height !== [] || age === ![]) {
			setAlert('values cannot be empty');
		} else {
			let bmi = (weight / (height * height)) * 703;
			setBmi('Your BMI is: ' + bmi.toFixed(1) + ' and ');

			if (bmi < 25) {
				setResult('You are underweight');
			} else if (bmi >= 25 && bmi < 30) {
				setResult('You are normal');
			} else {
				setResult('You are overweight');
			}
			if (gender === 'male') {
				let bodyFat = 1.2 * bmi + 0.23 * age - 10.8 * 1 - 5.4;
				setBodyFat(`* Your Body Fat is: ${bodyFat.toFixed(1)}%`);
				let bmr =
					66 + 13.7 * (weight * 0.453592) + 5 * (height * 2.54) - 6.8 * age;
				setBmr(
					'* Your body burns ' +
						bmr.toFixed(1) +
						' calories each day to keep you alive.'
				);

				let tdee = 1.5 * parseInt(bmr);
				setTdee(
					'* You need ' +
						tdee.toFixed(1) +
						' calories every day to maintain your current weight'
				);

				let idealWeight = 50 + 0.91 * (height * 2.54 - 152.4);
				let checkIdealWeight = () => {
					if (idealWeight < 0) {
						return setIdealWeight('* Your ideal weight can not be calculated');
					} else {
						return setIdealWeight(
							'* Your ideal weight is ' + idealWeight.toFixed(1)
						);
					}
				};
				checkIdealWeight();
			}
			if (gender === 'female') {
				let bodyFat = 1.2 * bmi + 0.23 * age - (10.8 * 0 - 5.4);
				setBodyFat(`*Your Body Fat is: ${bodyFat.toFixed(1)}%`);
				let bmr =
					655 + 9.6 * (weight * 0.453592) + 1.8 * (height * 2.54) - 4.7 * age;
				setBmr(
					'* Your body burns ' +
						bmr.toFixed(1) +
						' calories each day to keep you alive.'
				);
				let tdee = 1.5 * parseInt(bmr);
				setTdee(
					'* You need ' +
						tdee.toFixed(1) +
						' calories every day to maintain your current weight'
				);
				let idealWeight = 50 + 0.91 * (height * 2.54 - 152.4);
				let checkIdealWeight = () => {
					if (idealWeight < 0) {
						return setIdealWeight('* Your ideal weight can not be calculated');
					} else {
						return setIdealWeight(
							'* Your ideal weight is ' + idealWeight.toFixed(1)
						);
					}
				};
				checkIdealWeight();
			}
		}
	};


	let reset = () => {
		window.location.reload();
	};

	let UserData = [
		{
			id: 1,
			country: 'india',
			water: 220
		},
		{
			id: 2,
			country: 'China',
			water: 160,
		},
		{
			id: 3,
			country: 'United States',
			water: 50,
		},
		{
			id: 4,
			country: 'Mexico',
			water: 45,
